package Exercícios;

public class ExercícioForWhileEvoluindoPraFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int x = 0; x <= 70; x++) {
			if(x%5 == 0) {
				System.out.println(x + " É divisível por 5");
			}
			
			if (x%2 != 0) {
				System.out.println(x + " É um número Ímpar.");
			}
		}

	}

}
